import React, { useEffect, useState } from 'react';

export const Chechout = () => {
  const [cart, setCart] = useState([]);
  const [subtotal, setSubtotal] = useState(0);

  // Load cart items from localStorage on component mount
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(storedCart);

    // Calculate the subtotal
    const calculatedSubtotal = storedCart.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
    setSubtotal(calculatedSubtotal);
  }, []);

  // Load PayPal SDK script dynamically
  useEffect(() => {
    const script = document.createElement('script');
    script.src = "https://www.paypal.com/sdk/js?client-id=YOUR_PAYPAL_CLIENT_ID"; // Replace with your PayPal client ID
    script.async = true;
    document.body.appendChild(script);

    script.onload = () => {
      if (window.paypal) {
        window.paypal.Buttons({
          createOrder: (data, actions) => {
            return actions.order.create({
              purchase_units: [
                {
                  amount: {
                    value: subtotal.toFixed(2), // Subtotal as a string
                  },
                },
              ],
            });
          },
          onApprove: (data, actions) => {
            return actions.order.capture().then((details) => {
              alert(`Transaction completed by ${details.payer.name.given_name}`);
              // Optionally, you can handle order completion logic here.
            });
          },
          onError: (err) => {
            console.error("PayPal Checkout Error:", err);
            alert("An error occurred during the payment process.");
          },
        }).render('#paypal-button-container');
      }
    };

    return () => {
      document.body.removeChild(script);
    };
  }, [subtotal]);

  return (
    <section className="checkout spad">
      <div className="container">
        <div className="row">
          <div className="col-lg-12">
            <h6>
              <span className="icon_tag_alt" /> Have a coupon?{' '}
              <a href="#">Click here</a> to enter your code
            </h6>
          </div>
        </div>
        <div className="checkout__form">
          <h4>Billing Details</h4>
          <form action="#">
            <div className="row">
              <div className="col-lg-8 col-md-6">
                {/* Billing form fields */}
              </div>
              <div className="col-lg-4 col-md-6">
                <div className="checkout__order">
                  <h4>Your Order</h4>
                  <div className="checkout__order__products">
                    Products <span>Total</span>
                  </div>
                  <ul>
                    {cart.map((item, index) => (
                      <li key={index}>
                        {item.title} <span>${(item.price * item.quantity).toFixed(2)}</span>
                      </li>
                    ))}
                  </ul>
                  <div className="checkout__order__subtotal">
                    Subtotal <span>${subtotal.toFixed(2)}</span>
                  </div>
                  <div className="checkout__order__total">
                    Total <span>${subtotal.toFixed(2)}</span>
                  </div>
                  <div id="paypal-button-container" style={{ marginTop: '20px' }}></div>
                  <button type="submit" className="site-btn">
                    PLACE ORDER
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};
