import { data } from 'jquery';
import React, { useState, useEffect } from 'react';

export const FeaturedProduct = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);  // Adding loading state
    const [error, setError] = useState(null);     // Adding error state

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('https://fakestoreapi.com/products');
                if (!response.ok) throw new Error('Failed to fetch products');
                const data = await response.json();
                setProducts(data);
                setLoading(false);
            } catch (error) {
                setError(error.message);
                setLoading(false);
            }
        };
        fetchProducts();
    }, []);
    con
    return (
        <section className="featured spad">
            <div className="container">
                <div className="row">
                    <div className="col-lg-12">
                        <div className="section-title">
                            <h2>Featured Product</h2>
                        </div>
                        <div className="featured__controls">
                            <ul>
                                <li className="active" data-filter="*" onClick={()=>setProducts(data)}>All</li>
                                <li data-filter=".oranges" onClick={()=>filterProduct("men's clothing")}>Men's Clothing</li>
                                <li data-filter=".fresh-meat">Women's Clothing</li>
                                <li data-filter=".vegetables">Jewelery</li>
                                <li data-filter=".fastfood">Electonic</li>
                            </ul>
                        </div>
                    </div>
                </div>
                <div className="row featured__filter">
                    {/* Map through products and assign each one its own column */}
                    {products.map((product) => {
                        const { id, title, price, description, category, image } = product;
                        return (
                            <div
                                key={id}
                                className="col-lg-3 col-md-4 col-sm-6 mix oranges fresh-meat"
                            >
                                <div className="featured__item">
                                    <div
                                        className="featured__item__pic set-bg"
                                        style={{
                                            backgroundImage: `url(${image})`,
                                            backgroundSize: "contain",
                                            backgroundPosition: "center",
                                            height: "250px",
                                        }}
                                    >
                                        <ul className="featured__item__pic__hover">
                                            <li>
                                                <a href="#">
                                                    <i className="fa fa-heart" />
                                                </a>
                                            </li>
                                            <li>
                                                <a href="#">
                                                    <i className="fa fa-retweet" />
                                                </a>
                                            </li>
                                            <li>
                                                <a href="#">
                                                    <i className="fa fa-shopping-cart" />
                                                </a>
                                            </li>
                                        </ul>
                                    </div>
                                    <div className="featured__item__text">
                                        <h6>
                                            <a href="#">{title}</a>
                                        </h6>
                                        <h5>${price}</h5>
                                    </div>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </div>
        </section>
    );
};
